const mineflayer = require('mineflayer') // eslint-disable-line

/**
 * @param {mineflayer.Bot} bot // to enable intellisense
 */

module.exports = bot => {
bot.on('chat', (username, message) => {
    if (message === '!guard') {
        bot.chat('I am now guarding this area.');

        bot.on('spawn', (entity) => {
            if (entity.type === 'mob' && (entity.mobType === 'zombie' || entity.mobType === 'skeleton')) {
                bot.chat(`Enemy ${entity.mobType} spawned! Taking action.`);
                bot.attack(entity);
            }
        });
    }
});
}