const mineflayer = require('mineflayer') // eslint-disable-line

/**
 * @param {mineflayer.Bot} bot // to enable intellisense
 */

module.exports = bot => {
 bot.on('chat', (username, message) => {
     if (message === 'help') {

         bot.chat('For help command chek out :- https://github.com/Team-IC/mineboty/')
     }
    });
}